TSocket.example:
//#CLIENTSIDE

// Packet IDs coming from the server
enum PACKET_INC {
  RECEIVEHANDSHAKE = 1,
  REQUESTPLIST = 2
};

// Packet IDs sent out to the server
enum PACKET_OUT {
  SENDHANDSHAKE = 1,
  SENDPLIST = 2
};

function onCreated() {
  // Create custom TSocket Object
  this.socket = createTSocket("127.0.0.1", 12345);

  // Setup socket event handlers
  this.catchEvent(this.socket, "onConnect", "onHandleSocketEstablished");
  this.catchEvent(this.socket, "onReceiveData", "onHandleSocketIncomingPacket");
  this.catchEvent(this.socket, "onClose", "onHandleSocketClosure");

  // Establish the connection to the server
  this.socket.establish();
}

// Called when socket connection is closed
function onHandleSocketClosure(temp.sockObject) {
  player.chat = "The TSocket Connection has been closed.";
}

// Called when socket connection is established
function onHandleSocketEstablished(temp.sockObject) {
  player.chat = "TSocket Connection has been established.";
  this.socket.sendPacket(PACKET_OUT.SENDHANDSHAKE, player.account);
}

// Event triggered to handle incoming packets
function onHandleSocketIncomingPacket(temp.sockObject) {
  temp.buffer.loadjsonfromstring(this.socket.data);

  switch (temp.buffer.packet_id) {
    case PACKET_INC.RECEIVEHANDSHAKE:
      echo("Received handshake from server");
    break;
    case PACKET_INC.REQUESTPLIST:
      this.trigger("SendPlayers", null);
    break;
  }

  // Avoids packet merging
  this.socket.clearBuffer();
}

// Sends the players list to the server
function onSendPlayers() {

  temp.packet = {};
  temp.packet.players = {};

  // Loop through all players
  for (temp.pl: allplayers) {
    temp.packet.players.(@temp.pl.account) = {};
    temp.packet.players.(@temp.pl.account).account = temp.pl.account;
    temp.packet.players.(@temp.pl.account).nick = temp.pl.nick;
    temp.packet.players.(@temp.pl.account).level = temp.pl.levelname;
    temp.packet.players.(@temp.pl.account).pos = temp.pl.x SPC temp.pl.y;
    temp.packet.players.(@temp.pl.account).head = temp.pl.head;
    temp.packet.players.(@temp.pl.account).paused = (temp.pl.paused ? "True" : "False");
  }

  // Include server name in packet
  temp.packet.server = getServerName();
  this.socket.sendPacket(PACKET_OUT.SENDPLIST, temp.packet.savejsontostring(1));
}

function createTSocket(temp.ip, temp.port) {
  // Allow client to connect to this IP/Port
  Adventure_setAllowedSocketsConnect(temp.ip @ ":" @ temp.port);

  // Initialize TSocket object
  temp.socket = new TSocket();
  temp.socket._ip = temp.ip;
  temp.socket._port = temp.port;

  // Function to create a JSON packet string
  temp.socket.createPacket = function (temp.packet_id, temp.packet_data) {
    temp.packet = {};
    temp.packet.packet_id = temp.packet_id;
    temp.packet.packet_data = temp.packet_data;
    return temp.packet.savejsontostring(1);
  };

  // Function to send a packet
  temp.socket.sendPacket = function (temp.packet_id, temp.packet_data) {
    if (!this.isconnected) return;
    temp.packet = this.createPacket(temp.packet_id, temp.packet_data);
    this.send(temp.packet);
  };

  // Clears any leftover received data
  temp.socket.clearBuffer = function () {
    this.data = "";
  };

  // Connect the socket to destination
  temp.socket.establish = function () {
    if (this._ip == null || this._port < 1 || this._port > 65535) return;
    this.connect(this._ip, this._port);
  };

  return temp.socket;
}

